import { Props } from "./jsx";
/**
 * the core of the agnostic jsx template engine (render to string).
 *
 * @api public
 */
declare function jsxEngine(code: string, payload?: Props, path?: string): Promise<string>;
/**
 * a jsx component (file) reader.
 *
 * @api public
 */
declare function importComponent(path: string): string;
export * from './jsx';
declare const _default: {
    render: typeof jsxEngine;
    jsxEngine: typeof jsxEngine;
    importComponent: typeof importComponent;
};
export default _default;
